import React from "react";

const Body = (props) => {
  const posts = props.posts;

  return (
    <div className="container">
      I'm the body
      {posts &&
        posts.map((post) => {
          return (
          <div>
              <h1>{post.title}</h1>
              <p>{post.body}</p>
          </div>
          )
        })}
    </div>
  );
};

export default Body;
